// Extrair os dados de entrada enviados pelo Electron
const textoExtraido = $input.first().json.textoExtraido;
const fileName = $input.first().json.fileName;
const fileId = $input.first().json.fileId;

// Truncar texto se necessário (pois JSON não pode ser muito grande)
let textoTratado = textoExtraido;
if (textoExtraido && textoExtraido.toString().length > 12000) {
  textoTratado = textoExtraido.toString().substring(0, 12000) + '...';
}

return {
  textoParaAnalise: textoTratado,
  fileName: fileName,
  fileId: fileId
};

// Pegar a resposta do AI Agent
const conteudoGPT = $input.first().json.output;
const fileName = $input.first().json.fileName;
const fileId = $input.first().json.fileId;

// Construir a resposta para a aplicação Electron
return {
  conteudoGerado: conteudoGPT,
  status: "completed",
  fileName: fileName,
  fileId: fileId,
  message: "Conteúdo gerado com sucesso, aguardando processamento no sistema"
};
